str_rgb = str_rgb.replace("rgb(","");
str_rgb = str_rgb.replace(")","");
str_rgb = str_rgb.split(",");
var mode = flow.get("mode");
var payload = {"r": parseInt(str_rgb[0]), "g": parseInt(str_rgb[1]), "b": parseInt(str_rgb[2]), "mode": mode};
flow.set("mqtt_payload", payload)
msg.payload = payload;
return msg;
